import React, { useContext, useState } from "react";
import { useRouter } from "next/router";
import Button from "react-bootstrap/Button";
import styles from "./DobForm.module.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { DispatchContext } from "../contexts/FormContext";
import ProgressBar from "react-bootstrap/ProgressBar";
import Form from "react-bootstrap/Form";
import DatePicker from "react-datepicker";
import {QuestionCircle} from "react-bootstrap-icons";
import Modal from "./SecondaryEducationForm";


export default function DobForm() {
    const router = useRouter();
    const dispatch = useContext(DispatchContext);
    const [showDob, setShowDob] = useState(false);
    const handleCloseDob = () => setShowDob(false);

    const now = 35;
    const progressInstance = <ProgressBar style={{height: '0.5rem'}} now={now} />;
    const [startDate, setStartDate] = useState(new Date());
    return (
        <div>
            <br />
            <Row>
                <Col
                    xs={{ span: 10, offset: 1 }}
                    md={{ span: 10, offset: 1 }}
                    lg={{ span: 10, offset: 1 }}
                >
                    {progressInstance}
                </Col>
            </Row>
            <br />
            <Row>
                <Col>
                    <h2 className={styles.header}>What is your date of birth?</h2>
                </Col>
            </Row>
            <Container>
                <Form onSubmit={(e) => {
                    e.preventDefault();
                    console.log('form submit dob', startDate)
                    dispatch({ type: "DOB", dob: startDate });
                    router.push('/gender');
                }}>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }} lg={{ span: 6, offset: 3 }}>
                            <DatePicker
                                selected={startDate}
                                onChange={date => setStartDate(date)}
                                className={styles.dobCSS}
                            />
                        </Col>
                    </Row>
                    <br/>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }} lg={{ span: 6, offset: 3 }}>
                            <Button
                                className="align-button"
                                style={{ width: "100%" }}
                                variant="outline-primary"
                                size="lg"
                                type="submit"
                            >
                                Next
                            </Button>{" "}
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={{span: 8, offset: 2}} md={{ span: 6, offset: 3 }} lg={{ span: 6, offset: 3 }}
                             onClick={() => {setShowDob(true)}}>
                            <h5 className={styles.header}><u>Why do we ask about your date of birth?</u></h5>
                        </Col>
                        <Col xs={1} md={1} lg={1}>
                            <QuestionCircle
                                size={30}
                                onClick={() => {setShowDob(true)}}
                                style={{position: 'relative', top: '15px', right: '100%'}}
                            />
                        </Col>
                    </Row>
                </Form>
            </Container>
            {/*ask about children*/}
            <Modal show={showDob} onHide={handleCloseDob}>
                <Modal.Body>If your children’s post-secondary education is something you’re planning to pay for, we’ll want to make sure that your family will have enough money to cover the costs if you were no longer around.</Modal.Body>
                <Modal.Footer>
                    <Button variant="outline-primary" onClick={handleCloseDob}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}
