import React, {useContext, useEffect, useState} from "react";
import { useRouter } from "next/router";
import Button from "react-bootstrap/Button";
import styles from "./rentOrOwnForm.module.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { DispatchContext } from "../contexts/FormContext";
import ProgressBar from "react-bootstrap/ProgressBar";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";

export default function rentOrOwnForm() {
    const router = useRouter();
    const dispatch = useContext(DispatchContext);
    const [hasLifeInsurance, setHasLifeInsurance] = useState('');
    //has life insurance
    const [employerLifeInsurance, setEmployerLifeInsurance] = useState('');
    const [personalLifeinsurance, setPersonalLifeinsurance] = useState('');

    const now = 75;
    const progressInstance = <ProgressBar style={{height: '0.5rem'}} now={now} />;
    useEffect(() => {
        dispatch({ type: "HAS_LIFE_INSURANCE", hasLifeInsurance: hasLifeInsurance });
    }, [hasLifeInsurance]);


    useEffect(() => {
    }, [employerLifeInsurance]);

    useEffect(() => {
    }, [personalLifeinsurance]);

    return (
        <div>
            <br />
            <Row>
                <Col
                    xs={{ span: 10, offset: 1 }}
                    md={{ span: 10, offset: 1 }}
                    lg={{ span: 10, offset: 1 }}
                >
                    {progressInstance}
                </Col>
            </Row>
            <br />
            <h2 className={styles.header}>Do you have life insurance?</h2>
            <Container>
                <Row>
                    <Col xs={6} md={{ span: 6 }} lg={{ span: 3, offset: 3 }}>
                        <Button
                            className="align-button"
                            onClick={() => {
                                setHasLifeInsurance(true);
                            }}
                            style={{ width: "100%" }}
                            variant="outline-primary"
                            size="lg"
                        >
                            Yes
                        </Button>{" "}
                    </Col>
                    <Col xs={6} md={{ span: 6 }} lg={{ span: 3 }}>
                        <Button
                            className="align-button"
                            onClick={ () => {
                                setHasLifeInsurance(false);
                            }}
                            style={{ width: "100%" }}
                            variant="outline-primary"
                            size="lg"
                        >
                            No
                        </Button>{" "}
                    </Col>
                </Row>
                <Choose>
                    <When condition={hasLifeInsurance}>
                        <>
                            <br/>
                            <Row>
                                <Col
                                    xs={{span: 10, offset: 1}}
                                    md={{span: 10, offset: 1}}
                                    lg={{span: 10}}
                                >
                                    <h5 className={styles.h5}>Employer life insurance?</h5>
                                </Col>
                                <Col
                                    xs={{span: 8, offset: 2}}
                                    md={{span: 6, offset: 3}}
                                    lg={{span: 6, offset: 3}}
                                >
                                    <InputGroup className="mb-3">
                                        <InputGroup.Prepend>
                                            <InputGroup.Text>$</InputGroup.Text>
                                        </InputGroup.Prepend>
                                        <FormControl
                                            placeholder="Employer Life Insurance"
                                            aria-label="Employer Life Insurance"
                                            aria-describedby="basic-addon2"
                                            onChange={(e) => {setEmployerLifeInsurance(e.target.value)}}
                                        />
                                    </InputGroup>
                                </Col>
                            </Row>
                            <br/>
                            <Row>
                                <Col
                                    xs={{span: 8, offset: 2}}
                                    md={{span: 6, offset: 3}}
                                    lg={{span: 6, offset: 3}}
                                >
                                    <InputGroup className="mb-3">
                                        <InputGroup.Prepend>
                                            <InputGroup.Text>$</InputGroup.Text>
                                        </InputGroup.Prepend>
                                        <FormControl
                                            placeholder="Monthly Payment"
                                            aria-label="Monthly Payment"
                                            aria-describedby="basic-addon2"
                                            onChange={(e) => {setMonthlyPayment(e.target.value)}}
                                        />
                                    </InputGroup>
                                </Col>
                            </Row>
                            <br />
                            <Row>
                                <Col
                                    xs={{ span: 8, offset: 2 }}
                                    md={{ span: 6, offset: 3 }}
                                    lg={{ span: 6, offset: 3 }}
                                >
                                    <Button
                                        style={{ width: "100%" }}
                                        variant="outline-primary"
                                        size="lg"
                                        onClick={router.push('/lifeInsurance')}
                                    >
                                        Next
                                    </Button>{" "}
                                </Col>
                            </Row>
                        </>
                    </When>
                </Choose>
            </Container>
        </div>

    )
}
