import React, { useContext, useState } from "react";
import { useRouter } from "next/router";
import Button from "react-bootstrap/Button";
import styles from "./ChildrenForm.module.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { DispatchContext } from "../contexts/FormContext";
import FormControl from "react-bootstrap/FormControl";
import InputGroup from "react-bootstrap/InputGroup";
import ProgressBar from "react-bootstrap/ProgressBar";
import Form from "react-bootstrap/Form";

export default function ChildrenForm() {
    const [hasChildren, updateHasChildren] = useState(false);
    const [numberOfChildren, updateNumberOfChildren] = useState([
        { id: 1, age: undefined }
    ]);
    const now = 15;
    const progressInstance = <ProgressBar now={now} />;
    const dispatch = useContext(DispatchContext);

    useEffect(() => {
        console.log("numberOfChildren", numberOfChildren);
    }, [numberOfChildren]);

    function updateData(e) {
        const { id, value } = e.target;
        console.log(id);
        const itemIndex = numberOfChildren.findIndex(
            item => item.id === Number(id)
        );
        console.log("itemIndex", itemIndex);
        if (itemIndex !== -1) {
            const children = [...numberOfChildren];
            const child = children[itemIndex];
            children.splice(itemIndex, 1, { ...child, age: value });
            updateNumberOfChildren(children);
        }
    }

    return (
        <div>
            <br />
            <Row>
                <Col
                    xs={{ span: 6, offset: 3 }}
                    md={{ span: 6, offset: 3 }}
                    lg={{ span: 6, offset: 3 }}
                >
                    {progressInstance}
                </Col>
            </Row>
            <br />
            <Form
                onSubmit={e => {
                    e.preventDefault();
                }}
            >
                <br />
                <h2 className={styles.header}>Do you have children?</h2>
                <Container>
                    <Row>
                        <Col xs={6} md={{ span: 6 }} lg={{ span: 3, offset: 3 }}>
                            <Button
                                className="align-button"
                                onClick={async () => {
                                    await updateHasChildren(true);
                                }}
                                style={{ width: "100%" }}
                                variant="outline-primary"
                                size="lg"
                            >
                                Yes
                            </Button>{" "}
                        </Col>
                        <Col xs={6} md={{ span: 6 }} lg={{ span: 3 }}>
                            <Button
                                className="align-button"
                                onClick={async e => {
                                    e.preventDefault();
                                    updateHasChildren(false);
                                }}
                                style={{ width: "100%" }}
                                variant="outline-primary"
                                type="submit"
                                size="lg"
                            >
                                No
                            </Button>{" "}
                        </Col>
                    </Row>
                </Container>
            </Form>
            {hasChildren === true && (
                <Form
                    onSubmit={e => {
                        e.preventDefault();
                    }}
                >
                    <br />
                    <Row>
                        <Col
                            xs={{ span: 12 }}
                            md={{ span: 6, offset: 3 }}
                            lg={{ span: 6, offset: 3 }}
                        >
                            <h2>How old are your children?</h2>
                        </Col>
                    </Row>
                    <Row>
                        {numberOfChildren.map((child, index) => (
                            <>
                            <div key={index}>
                                <Col
                                    xs={{ span: 4, offset: 2 }}
                                    md={{ span: 3, offset: 3 }}
                                    lg={{ span: 3, offset: 3 }}
                                >
                                    <h5 className={styles.header}>Child #{index + 1}</h5>
                                </Col>
                                <Col
                                    xs={{ span: 4 }}
                                    md={{ span: 3 }}
                                    lg={{ span: 3 }}
                                    key={index + 20}
                                >
                                    <InputGroup className="mb-3">
                                        <FormControl
                                            id={index + 1}
                                            placeholder="Age"
                                            aria-label="Age"
                                            aria-describedby="basic-addon2"
                                            onChange={e => updateData(e)}
                                        />
                                    </InputGroup>
                                </Col>
                            </div>
                            </>
                        ))}
                    </Row>
                    <Row>
                        <Col
                            xs={{ span: 8, offset: 2 }}
                            md={{ span: 6, offset: 3 }}
                            lg={{ span: 6, offset: 3 }}
                        >
                            <Button
                                className="align-button"
                                onClick={e => {
                                    e.preventDefault();
                                    updateNumberOfChildren([
                                        ...numberOfChildren,
                                        { id: numberOfChildren.length + 1, age: undefined }
                                    ]);
                                }}
                                style={{ width: "100%" }}
                                variant="outline-primary"
                                type="submit"
                                size="lg"
                            >
                                Add Child
                            </Button>{" "}
                        </Col>
                    </Row>
                    <br />
                    <Row>
                        <Col
                            xs={{ span: 8, offset: 2 }}
                            md={{ span: 6, offset: 3 }}
                            lg={{ span: 6, offset: 3 }}
                        >
                            <Button
                                style={{ width: "100%" }}
                                type="submit"
                                variant="outline-primary"
                                size="lg"
                            >
                                Next
                            </Button>{" "}
                        </Col>
                    </Row>
                </Form>
            )}
        </div>
    );
}
