import React, {useContext} from 'react';
import Button from 'react-bootstrap/Button';
import styles from './SpouseForm.module.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {DispatchContext} from "../contexts/FormContext";

export default function SpouseForm() {
    const dispatch = useContext(DispatchContext);
    let hasChildren = false;
    if (hasChildren) {
        children = <Row>
            <Col xs={6} md={6} lg={6}>
                <h3 className={styles.header}></h3>
            </Col>
        </Row>;
    }
    return (
        <div>
            <h1 className={styles.header}>Do you have a spouse or partner?</h1>
            <Container>
                <Row>
                    <Col xs={6} md={6} lg={6}>
                        <Button
                            onClick={(e) => {
                                dispatch({type: "ADD_SPOUSE", spouse: true});
                            }}
                            style={{width: '100%'}}
                            variant="outline-primary"
                            size="lg" >Yes</Button>{' '}
                    </Col>
                    <Col xs={6} md={6} lg={6}>
                        <Button
                            onClick={(e) => {
                                dispatch({type: "ADD_SPOUSE", spouse: false});
                            }}
                            style={{width: '100%'}}
                            variant="outline-primary"
                            size="lg" >No</Button>{' '}
                    </Col>
                </Row>
                {children}
            </Container>
        </div>
    )
}
