const reducer = (state, action) => {
    let key;
    switch(action.type){
        case "ADD_SPOUSE":
            key = "spouse";
            if (state.hasOwnProperty(key)) {
                let updatedVal = action.spouse;
                return state.spouse = updatedVal;
            } else {
                return {...state, spouse: action.spouse}
            }
        case "SPOUSE_AGE":
            key = "spouseAge";
            if (state.hasOwnProperty(key)) {
