        //         </Col>
        //     </Row>;
        //     spouseAge = <>
        //         <Row>
        //         <Col xs={12} md={12} lg={{span:6, offset: 3}}>
        //             <InputGroup className="mb-3">
        //                 <InputGroup.Prepend>
        //                     <InputGroup.Text>Age</InputGroup.Text>
        //                 </InputGroup.Prepend>
        //                 <FormControl/>
        //             </InputGroup>
        //         </Col>
        //     </Row>
        //         <Row>
        //             <Col xs={12} md={12} lg={{span:6, offset: 3}}>
        //                 <Button
        //                     onClick={(e) => {
        //                         e.preventDefault()
        //                         // dispatch({type: "SPOUSE_AGE", spouseAge: e.target.value});
        //
        //                     }}
        //                     style={{width: '100%'}}
        //                     type="submit"
        //                     variant="outline-primary"
        //                     size="lg" >Next</Button>{' '}
        //             </Col>
        //         </Row>
        //     </>;
        // }
        return (
            <div>
                <Form onSubmit={(e) => {router.push('/children')}}>

                <br/>
                <Row>
                    <Col xs={{span:6, offset: 3}} md={{span:6, offset: 3}} lg={{span:6, offset: 3}}>
                        <ProgressBar variant="success" now={10} />
                    </Col>
                </Row>
                <br/>
                <h2 className={styles.header}>Do you have a spouse or partner?</h2>
                <Container>
                    <Row>
                        <Col xs={6} md={{span:6, offset: 3}} lg={{span:6, offset: 3}}>
                            <Button
                                className="align-button"
                                onClick={() => {
                                    this.setState({hasSpouse: true})
                                    // dispatch({type: "ADD_SPOUSE", spouse: true});
                                    // updateHasSpouse(true);
                                }}
                                style={{width: '100%'}}
                                variant="outline-primary"
                                size="lg" >Yes</Button>{' '}
                        </Col>
                        <Col xs={6} md={{span:6, offset: 3}} lg={{span:6, offset: 3 }}>
                            <Button
                                className="align-button"
                                onClick={() => {
                                    this.setState({hasSpouse: false})
                                    // dispatch({type: "ADD_SPOUSE", spouse: false});
                                    // router.push('/children')
                                }}
                                style={{width: '100%'}}
                                variant="outline-primary"
                                size="lg" >No</Button>{' '}
                        </Col>
                    </Row>
                    {/*{spouse}*/}
                    <Row>
                        <Col xs={12} md={12} lg={{span:6, offset: 3}}>
                            <br/><br/>
                            <h3 className={styles.header}>How old is your spouse or partner?</h3>
                        </Col>
                    </Row>
                    {/*{spouseAge}*/}
                    <>
                        <Row>
                            <Col xs={12} md={12} lg={{span:6, offset: 3}}>
                                <InputGroup className="mb-3">
                                    <InputGroup.Prepend>
                                        <InputGroup.Text>Age</InputGroup.Text>
                                    </InputGroup.Prepend>
                                    <FormControl/>
                                </InputGroup>
                            </Col>
                        </Row>
                        <Row>
                            <Col xs={12} md={12} lg={{span:6, offset: 3}}>
                                <Button
                                    onClick={(e) => {
                                        e.preventDefault()
                                        // dispatch({type: "SPOUSE_AGE", spouseAge: e.target.value});

                                    }}
                                    style={{width: '100%'}}
                                    type="submit"
                                    variant="outline-primary"
                                    size="lg" >Next</Button>{' '}
                            </Col>
                        </Row>
                    </>
                </Container>
                </Form>

            </div>
        )
    }
}

export default withRouter(SpouseForm)

