import React, {useContext, setState} from 'react';
import Button from 'react-bootstrap/Button';
import styles from './SpouseForm.module.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {DispatchContext} from "../contexts/FormContext";
import { useRouter } from 'next/router';

export default function SpouseForm() {
    const dispatch = useContext(DispatchContext);
    const router = useRouter();
    let hasChildren = false;
    let children;
    // if (hasChildren) {
    //     children = <Row>
    //         <Col xs={6} md={6} lg={6}>
    //             <h2 className={styles.header}>How old is your spouse or partner?</h2>
    //         </Col>
    //     </Row>;
    // }
    return (
        <div>
            <h1 className={styles.header}>Do you have a spouse or partner?</h1>
            <Container>
                <Row>
                    <Col xs={6} md={6} lg={6}>
                        <Button
                            onClick={(e) => {
                                dispatch({type: "ADD_SPOUSE", spouse: true});
                                // hasChildren = true;
                            }}
                            style={{width: '100%'}}
                            variant="outline-primary"
                            size="lg" >Yes</Button>{' '}
                    </Col>
                    <Col xs={6} md={6} lg={6}>
                        <Button
                            onClick={(e) => {
                                dispatch({type: "ADD_SPOUSE", spouse: false});
                                router.push('/children')
                            }}
                            style={{width: '100%'}}
                            variant="outline-primary"
                            size="lg" >No</Button>{' '}
                    </Col>
                </Row>
                {children}
            </Container>
        </div>
    )
}
