import React, {useContext, useEffect, useState} from "react";
import { useRouter } from "next/router";
import Button from "react-bootstrap/Button";
import styles from "./SpouseForm.module.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import {DispatchContext, FormContext} from "../contexts/FormContext";
import FormControl from "react-bootstrap/FormControl";
import InputGroup from "react-bootstrap/InputGroup";
import ProgressBar from "react-bootstrap/ProgressBar";
import Form from "react-bootstrap/Form";
import { QuestionCircle } from 'react-bootstrap-icons'
import Modal from "react-bootstrap/Modal"

function SpouseForm() {
    let [hasSpouse, updateHasSpouse] = useState('');
    let [spouseAge, updateSpouseAge] = useState("");
    const router = useRouter();
    const dispatch = useContext(DispatchContext);
    const form = useContext(FormContext);
    const now = 10;
    const progressInstance = <ProgressBar style={{height: '0.5rem'}} now={now} />;
    // modal stuff
    const [showTitle, setShowTitle] = useState(false);
    const handleCloseTitle = () => setShowTitle(false);
    const handleShowTitle = () => setShowTitle(true);

    useEffect(() => {
        dispatch({ type: "ADD_SPOUSE", spouse: hasSpouse });
        }, [hasSpouse]);

    console.log('FORM', form);
    return (
        <div>
            <br />
            <Row>
                <Col
                    xs={{ span: 10, offset: 1 }}
                    md={{ span: 10, offset: 1 }}
                    lg={{ span: 10, offset: 1 }}
                >
                    {progressInstance}
                </Col>
            </Row>
            <br />

            <Form
                onSubmit={(e) => {
                    e.preventDefault();
                    console.log(e);
                    router.push("/children");
                }}
            >
                <br />
                <Row>
                    <Col xs={{span: 6, offset: 3}} md={{ span: 6, offset: 3 }} lg={{ span: 6, offset: 3 }}>
                        <h2 className={styles.header}>Do you have a spouse or partner?</h2>
                    </Col>
                    <Col xs={1} md={1} lg={1}>
                        <QuestionCircle
                            size={30}
                            onClick={() => {handleShowTitle}}
                            style={{position: 'relative', top: '15px'}}
                        />
                    </Col>
                </Row>
                <Container>
                    <Row>
                        <Col xs={6} md={{ span: 6 }} lg={{ span: 3, offset: 3 }}>
                            <Button
                                className="align-button"
                                onClick={ () => {
                                    updateHasSpouse(true);
                                }}
                                style={{ width: "100%" }}
                                variant="outline-primary"
                                size="lg"
                            >
                                Yes
                            </Button>{" "}
                        </Col>
                        <Col xs={6} md={{ span: 6 }} lg={{ span: 3 }}>
                            <Button
                                className="align-button"
                                onClick={ (e) => {
                                    e.preventDefault();
                                    updateHasSpouse(false);
                                    router.push("/children");
                                }}
                                style={{ width: "100%" }}
                                variant="outline-primary"
                                type="submit"
                                size="lg"
                            >
                                No
                            </Button>{" "}
                        </Col>
                    </Row>
                </Container>
            </Form>
            {hasSpouse === true && (
                <Container>
                    <Row>
                        <Col xs={12} md={12} lg={{ span: 6, offset: 3 }}>
                            <br />
                            <br />
                            <h3 className={styles.header}>
                                How old is your spouse or partner?
                            </h3>
                        </Col>
                    </Row>
                    <Form
                        onSubmit={(e) => {
                            e.preventDefault();
                            dispatch({ type: "SPOUSE_AGE", spouseAge: spouseAge });
                            router.push("/children");
                        }}
                    >
                        <Row>
                            <Col xs={12} md={12} lg={{ span: 6, offset: 3 }}>
                                <InputGroup className="mb-3">
                                    <InputGroup.Prepend>
                                        <InputGroup.Text>Age</InputGroup.Text>
                                    </InputGroup.Prepend>
                                    <FormControl
                                        onChange={(e) => {
                                            updateSpouseAge(e.target.value);
                                        }}
                                    />
                                </InputGroup>
                            </Col>
                        </Row>
                        <Row>
                            <Col xs={12} md={12} lg={{ span: 6, offset: 3 }}>
                                <Button
                                    style={{ width: "100%" }}
                                    type="submit"
                                    variant="outline-primary"
                                    size="lg"
                                >
                                    Next
                                </Button>{" "}
                            </Col>
                        </Row>
                    </Form>
                </Container>
            )}
            {/*title modal*/}
            <Modal show={showTitle} onHide={handleCloseTitle}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseTitle}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default SpouseForm;
