                    {progressInstance}
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 8, offset: 2 }}
                    md={{ span: 8, offset: 3 }}
                    lg={{ span: 8, offset: 2 }}
                >
                    <h2 className={styles.header}>What are your monthly expenses</h2>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 4, offset: 1 }}
                    md={{ span: 2, offset: 2 }}
                    lg={{ span: 2, offset: 2 }}
                >
                    <h5 className={styles.h5}>Mortgage and rent</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
                >
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Mortgage and rent"
                            defaultValue={expenseMortgageAndRent}
                            aria-label="Mortgage and rent"
                            aria-describedby="basic-addon2"
                            onChange={(e) => {
                                setExpenseMortgageAndRent(e.target.value);
                            }}
                        />
                    </InputGroup>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 4, offset: 1 }}
                    md={{ span: 2, offset: 2 }}
                    lg={{ span: 2, offset: 2 }}
                >
                    <h5 className={styles.h5}>Housing bills & utilities</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
