                    <h5 className={styles.h5}>Phone, Internet, Cable</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
                >
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Home Internet Cable"
                            aria-label="Home Internet Cable"
                            aria-describedby="basic-addon2"
