                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Shopping, fitness & grooming"
                            aria-label="Shopping, fitness & grooming"
                            aria-describedby="basic-addon2"
                            onChange={(e) => {
                            }}
                        />
