import React, {useContext, useState, Component} from 'react';
import Button from 'react-bootstrap/Button';
import styles from './SpouseForm.module.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {DispatchContext} from "../contexts/FormContext";
import FormControl from 'react-bootstrap/FormControl';
import InputGroup from 'react-bootstrap/InputGroup';
import { useRouter } from 'next/router'
import ProgressBar from 'react-bootstrap/ProgressBar';
import Form from 'react-bootstrap/Form';

export default class SpouseForm extends Component {
    constructor(props) {
        super(props)
        this.state = {
            hasSpouse: false,
            spouseAge: null
        }
    }
    render() {
        // const dispatch = useContext(DispatchContext);
        // let [hasSpouse, updateHasSpouse] = useState(false);
        // const router = useRouter();
        let spouse;
        let spouseAge;
        let nextPage;
        if (this.state.hasSpouse) {
            spouse = <Row>
                <Col xs={12} md={12} lg={{span:6, offset: 3}}>
                    <br/><br/>
                    <h3 className={styles.header}>How old is your spouse or partner?</h3>
                </Col>
            </Row>;
            spouseAge = <><Form><Row>
                <Col xs={12} md={12} lg={{span:6, offset: 3}}>
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>Age</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl/>
                    </InputGroup>
                </Col>
            </Row>
                <Row>
                    <Col xs={12} md={12} lg={{span:6, offset: 3}}>
                        <Button
                            onClick={(e) => {
                                e.preventDefault()
                                // dispatch({type: "SPOUSE_AGE", spouseAge: e.target.value});
                                // router.push('/children')
                            }}
                            style={{width: '100%'}}
                            type="submit"
                            variant="outline-primary"
                            size="lg" >Next</Button>{' '}
                    </Col>
                </Row>
            </Form>
            </>;
        }
        return (
            <div>
                <br/>
                <Row>
                    <Col xs={{span:6, offset: 3}} md={{span:6, offset: 3}} lg={{span:6, offset: 3}}>
                        <ProgressBar variant="success" now={10} />
                    </Col>
                </Row>
                <br/>
                <h2 className={styles.header}>Do you have a spouse or partner?</h2>
                <Container>
                    <Row>
                        <Col xs={6} md={6} lg={{span:6}}>
                            <Button
                                className="align-button"
                                onClick={() => {
                                    // dispatch({type: "ADD_SPOUSE", spouse: true});
                                    // updateHasSpouse(true);
                                }}
                                style={{width: '100%'}}
                                variant="outline-primary"
                                size="lg" >Yes</Button>{' '}
                        </Col>
                        <Col xs={6} md={6} lg={{span:6}}>
                            <Button
                                className="align-button"
                                onClick={() => {
                                    // dispatch({type: "ADD_SPOUSE", spouse: false});
                                    // router.push('/children')
                                }}
                                style={{width: '100%'}}
                                variant="outline-primary"
                                size="lg" >No</Button>{' '}
                        </Col>
                    </Row>
                    {spouse}
                    {spouseAge}

                </Container>
            </div>
        )
    }
}

