import React, {useContext, useEffect, useState} from "react";
import { useRouter } from "next/router";
import Button from "react-bootstrap/Button";
import styles from "./rentOrOwnForm.module.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { DispatchContext } from "../contexts/FormContext";
import ProgressBar from "react-bootstrap/ProgressBar";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";

export default function MonthlyExpensesForm() {
    const router = useRouter();
    const dispatch = useContext(DispatchContext);
    const now = 95;
    const progressInstance = <ProgressBar style={{height: '0.5rem'}} now={now} />;

    return (
        <div>
            <br />
            <Row>
                <Col
                    xs={{ span: 10, offset: 1 }}
                    md={{ span: 10, offset: 1 }}
                    lg={{ span: 10, offset: 1 }}
                >
                    {progressInstance}
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 8, offset: 2 }}
                    md={{ span: 8, offset: 3 }}
                    lg={{ span: 8, offset: 4 }}
                >
                    <h5>Estimate your debts for each category</h5>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 4, offset: 1 }}
                    md={{ span: 2, offset: 2 }}
                    lg={{ span: 2, offset: 2 }}
                >
                    <h5 className={styles.h5}>Credit cards</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
                >
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Credit card"
                            aria-label="Credit card"
                            aria-describedby="basic-addon2"
                            onChange={(e) => {
                                setDebtCreditCard(e.target.value)
                            }}
                        />
                    </InputGroup>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 4, offset: 1 }}
                    md={{ span: 2, offset: 2 }}
                    lg={{ span: 2, offset: 2 }}
                >
                    <h5 className={styles.h5}>Student loans</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
                >
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Student loans"
                            aria-label="Student loans"
                            aria-describedby="basic-addon2"
                            onChange={(e) => {
                                setDebtStudentLoan(e.target.value)
                            }}
                        />
                    </InputGroup>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 4, offset: 1 }}
                    md={{ span: 2, offset: 2 }}
                    lg={{ span: 2, offset: 2 }}
                >
                    <h5 className={styles.h5}>Home equity loans</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
                >
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Home equity loans"
                            aria-label="Home equity loans"
                            aria-describedby="basic-addon2"
                            onChange={(e) => {
                                setDebtHomeEquity(e.target.value)
                            }}
                        />
                    </InputGroup>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 4, offset: 1 }}
                    md={{ span: 2, offset: 2 }}
                    lg={{ span: 2, offset: 2 }}
                >
                    <h5 className={styles.h5}>Lines of credit</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
                >
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Lines of credit"
                            aria-label="Lines of credit"
                            aria-describedby="basic-addon2"
                            onChange={(e) => {
                                setDebtLineOfCredit(e.target.value)
                            }}
                        />
                    </InputGroup>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 4, offset: 1 }}
                    md={{ span: 2, offset: 2 }}
                    lg={{ span: 2, offset: 2 }}
                >
                    <h5 className={styles.h5}>Other debt</h5>
                </Col>
                <Col
                    xs={{ span: 6 }}
                    md={{ span: 6 }}
                    lg={{ span: 6 }}
                >
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>$</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                            placeholder="Other debt"
                            aria-label="Other debt"
                            aria-describedby="basic-addon2"
                            onChange={(e) => {
                                setDebtOther(e.target.value)
                            }}
                        />
                    </InputGroup>
                </Col>
            </Row>
            <br/>
            <Row>
                <Col
                    xs={{ span: 8, offset: 2 }}
                    md={{ span: 6, offset: 3 }}
                    lg={{ span: 6, offset: 3 }}
                >
                    <Button
                        style={{ width: "100%" }}
                        variant="outline-primary"
                        size="lg"
                        onClick={()=> {router.push('/monthlyExpenses')}}
                    >
                        Next
                    </Button>{" "}
                </Col>
            </Row>
        </div>
    )
}
