import React, {useContext, useState} from 'react';
import Button from 'react-bootstrap/Button';
import styles from './SpouseForm.module.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {DispatchContext} from "../contexts/FormContext";
import FormControl from 'react-bootstrap/FormControl';
import InputGroup from 'react-bootstrap/InputGroup';
export default function SpouseForm() {
    const dispatch = useContext(DispatchContext);
    let [hasSpouse, updateHasSpouse] = useState(false);
    let spouse;
    let spouseAge;
    if (hasSpouse) {
        spouse = <Row>
            <Col xs={12} md={12} lg={12}>
                <br/><br/>
                <h3 className={styles.header}>How old is your spouse or partner?</h3>
            </Col>
        </Row>;
        spouseAge = <Row>
            <Col xs={12} md={12} lg={12}>
                <InputGroup className="mb-3">
                    <InputGroup.Prepend>
                        <InputGroup.Text>Age</InputGroup.Text>
                    </InputGroup.Prepend>
                </InputGroup>
            </Col>
        </Row>
    }
    return (
        <div>
            <h1 className={styles.header}>Do you have a spouse or partner?</h1>
            <Container>
                <Row>
                    <Col xs={6} md={6} lg={6}>
                        <Button
                            onClick={(e) => {
                                dispatch({type: "ADD_SPOUSE", spouse: true});
                                updateHasSpouse(true);
                            }}
                            style={{width: '100%'}}
                            variant="outline-primary"
                            size="lg" >Yes</Button>{' '}
                    </Col>
                    <Col xs={6} md={6} lg={6}>
                        <Button
                            onClick={(e) => {
                                dispatch({type: "ADD_SPOUSE", spouse: false});
                            }}
                            style={{width: '100%'}}
                            variant="outline-primary"
                            size="lg" >No</Button>{' '}
                    </Col>
                </Row>
                {spouse}
                {spouseAge}
            </Container>
        </div>
    )
}
